#; classes s rs = ##class(%ResultSet).%New("%Dictionary.ClassDefinition:Summary") if rs.Execute() {
while rs.%Next(){
s code = rs.Name _ ".cls", isSystem = rs.System if ( 'system && isSystem ) continue if ( 'generated && $$$isGenerated( code ) ) continue if ( 'percent && $$$isPercented( code ) ) continue if ( 'mapped && $$$isMapped( code ) ) continue s filename = ..filename( code ) $$$log w $$$export( code, filename )
} s rs="" }
